package Programs_IQ;

public class _29NumberRow {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n = 1;
		
        
		for(int i = 0; i <= 2; i++){
			 
            	 for(int j = 0; j <= i; j++ ){
            		
            		 System.out.print(n + " ");
            		 n = n + 1;
            	 }
            	System.out.println(" ");
             }
	
	}

}
